import React, { Component } from "react";
import Search from "./Search/Search";
import HeaderMenu from "./Menu";
import { Link } from "react-router-dom";
import { Input, Menu, Container, Icon, Sidebar } from "semantic-ui-react";
import styled from "@emotion/styled";
import "reset-css";

const HeaderWrapper = styled.div`
  width: 100% !important;
`;

class Header extends Component {
  state = { activeItem: "home", isLoading: false, results: [], value: "" };

  handleItemClick = (e, { name }) => this.setState({ activeItem: name });
  render() {
    const { activeItem } = this.state;
    return (
      <div>
        <HeaderWrapper>
          <Container>
            <Menu secondary>
              {/* Logotype */}
              <Menu.Item
                as={Link}
                to="/"
                name="home"
                onClick={this.handleItemClick}
              >
                Logotype
              </Menu.Item>
              {/* Logotype */}
              <Menu.Menu position="right">
                <Search />
                <Menu.Item
                  as={Link}
                  to="/account"
                  name="account"
                  active={activeItem === "account"}
                  onClick={this.handleItemClick}
                >
                  <Icon name="user" size="large" title="My account" />
                </Menu.Item>
                <Menu.Item
                  as={Link}
                  to="/cart"
                  name="cart"
                  active={activeItem === "cart"}
                  onClick={this.handleItemClick}
                >
                  <Icon name="shopping cart" size="large" title="My cart" />
                </Menu.Item>
              </Menu.Menu>
            </Menu>
            {/* <HeaderSidebar /> */}
          </Container>
        </HeaderWrapper>
        <HeaderMenu />
      </div>
    );
  }
}

export default Header;
