import React, { Component } from "react";
import { Menu, Container, Icon } from "semantic-ui-react";
import styled from "@emotion/styled";
import "./HeaderCart.css";

const HeaderCartCount = styled.div`
  background-color: red;
  height: 10px;
  width: 10px;
`;

export default class HeaderCart extends Component {
  state = { activeItem: "home", cartCount: 0 };

  handleItemClick = (e, { name }) => this.setState({ activeItem: name });

  render() {
    const { activeItem } = this.state;

    return (
      <div className="HeaderCart">
        <Icon name="shopping cart" size="large" title="My cart">
          Cart
          <HeaderCartCount className="HeaderCartCount">
            {this.state.cartCount}
          </HeaderCartCount>
        </Icon>
      </div>
    );
  }
}
